import io from 'socket.io-client';
import Ip from './config';
console.log(Ip);
class socketConnect {
    constructor() {
        this.connections = {};
    }

    connection(token) {
        this.connections = io(`${Ip}`, token ? { query: { token: token } } : null);
        return this.connections
    }

}
const socket = new socketConnect();

export default socket
