import React, { Component } from 'react'
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import Io from '../connection';
import { Redirect } from 'react-router';
import { isNullOrUndefined } from 'util';
let io = null;
class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            message: {},
            redirect: false,
            userId: '',
        }
    }
    componentDidMount() {
        const chatArea = document.querySelector(".chatArea");
        chatArea.scrollTop = chatArea.scrollHeight
        const token = localStorage.getItem('token');
        if (!isNullOrUndefined(token)) {
            io = Io.connectionsRoom('Chat', token);
            io.on('error', (errorInfo) => {
                this.setState({
                    redirect: true,
                });
            });
            io.emit('allMessage');
            io.on('returnedMessage', (chat) => {
                this.setState({
                    userId: chat.userId,
                    message: chat.messageInfo
                });

            });

        } else {
            this.setState({
                redirect: true,
            })
        }

    }

    onChangeEventChat = (e) => {
        const message = e.target.value;
        this.setState({
            message
        });
    }

    onClickEventSend = (e) => {
        e.preventDefault();
        io.emit('chatMessage', { message: this.state.message });
    }

    chatMessageShow = () => {
        const message = this.state.message
        const chatMessage = [];
        console.log(message);
        if (message !== {}) {
            message.forEach((messageInfo, key) => {
                if (this.state.userId !== messageInfo.userId) {
                    chatMessage.push(
                        <div className="incoming-msg">
                            <div className="incoming-msg-user">Enis Keskin</div>
                            <span className="chat-msg">Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden </span>
                            <div className="incoming-msg-date">23:05</div>
                        </div>)
                } else {
                    chatMessage.push(
                        <div className="outgoing-msg">
                            <div className="outgoing-msg-user">Enis Keskin</div>
                            <span className="chat-msg">Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden </span>
                            <div className="outgoing-msg-date">23:05</div>
                        </div>)
                }
            });
            return chatMessage;
        }

    }

    render() {
        return (
            <div className="mainChat">
                {this.state.redirect ?
                    <Redirect to='/' />
                    :
                    <div className="chat">
                        <div className="chatArea">
                            {this.chatMessageShow()}
                        </div>
                        <InputGroup className="chatInput" onChange={this.onChangeEventChat.bind(this)}>
                            <FormControl placeholder="Mesaj" />
                            <InputGroup.Append>
                                <Button variant="primary" onClick={this.onClickEventSend.bind(this)}>GÃ¶nder</Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </div>
                }
            </div>

        )
    }
}

export default Chat;
