import React, { Component } from 'react'
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import Io from '../connection';
import { Redirect } from 'react-router';
import { isNullOrUndefined } from 'util';
import Form from 'react-bootstrap/FormGroup';
let io = null;
class Chat extends Component {
    constructor(props) {
        super(props);
        this.sendMessage = '';
        this.state = {
            getMessage: {},
            redirect: false,
            userId: '',
        }
    }
    componentDidMount() {
        const chatArea = document.querySelector(".chatArea");
        chatArea.scrollTop = chatArea.scrollHeight

        const token = localStorage.getItem('token');
        if (!isNullOrUndefined(token)) {

            io = Io.connectionsRoom('Chat', token);

            io.on('error', () => {
                this.setState({
                    redirect: true,
                });
            });

            io.emit('allMessage');

            io.on('returnedMessage', (chat) => {
                this.setState({
                    userId: chat.userId,
                    getMessage: chat.messageInfo
                });

            });

        } else {
            this.setState({
                redirect: true,
            })
        }

    }

    onChangeEventChat = (e) => {
        const message = e.target.value;
        this.sendMessage = message
    }

    onClickEventSend = (e) => {
        e.preventDefault();
        io.emit('chatMessage', { message: this.sendMessage });
    }

    isEmpty = (obj) => {
        for (var key in obj) {
            if (obj.hasOwnProperty(key))
                return false;
        }
        return true;
    }

    chatMessageShow = () => {
        const message = this.state.getMessage;
        const chatMessage = [];
        if (!this.isEmpty(message)) {
            message.forEach((messageInfo, key) => {
                if (this.state.userId !== messageInfo.userId) {
                    chatMessage.push(
                        <div className="incoming-msg" key={key}>
                            <div className="incoming-msg-user">Enis Keskin</div>
                            <span className="chat-msg">Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden </span>
                            <div className="incoming-msg-date">23:05</div>
                        </div>)
                } else {
                    chatMessage.push(
                        <div className="outgoing-msg" key={key}>
                            <div className="outgoing-msg-user">Enis Keskin</div>
                            <span className="chat-msg">Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden Giden </span>
                            <div className="outgoing-msg-date">23:05</div>
                        </div>)
                }
            });
            return chatMessage;
        }

    }

    render() {
        return (
            <div className="mainChat">
                {this.state.redirect ?
                    <Redirect to='/' />
                    :
                    <div className="chat">
                        <div className="chatArea">
                            {this.chatMessageShow()}
                        </div>
                        <Form>
                            <InputGroup className="chatInput" >
                                <FormControl placeholder="Mesaj" onChange={this.onChangeEventChat.bind(this)} />
                                <InputGroup.Append>
                                    <Button variant="primary" onClick={this.onClickEventSend.bind(this)}>GÃ¶nder</Button>
                                </InputGroup.Append>
                            </InputGroup>
                        </Form>
                    </div>
                }
            </div>

        )
    }
}

export default Chat;
