    }

    onChangeEventChat = (e) => {
        const message = e.target.value;
        this.setState({
            message
        })
    }

    onClickEventSend = (e) => {
        e.preventDefault();
        io.emit('chatMessage', { message: this.state.message });
    }

    render() {
        return (
            <div className="mainChat">
                {this.state.redirect ?
                    <Redirect to='/' />
